<style lang="sass" >
@import ../../../assets/sass/variables


.impact-badge
  stroke: #a5a5a5
  stroke-width: 1px
  margin-top: 2px

.level-high
  fill: $level-high-color

.level-likely-high
  fill:  $level-likely-high-color

.level-medium, .level-unknown-significance, .level-unknown
  fill: $level-medium-color

.level-low
  fill: $level-low-color

.level-likely-low
  fill: $level-likely-low-color

.level-conflicting
  fill: $level-conflicting-color

.level-other
  fill: $level-other-color

#not-significant-symbol
  circle
    fill: $not-significant-color
  path
    fill: white

#user-flagged-symbol
  fill: $link-color !important

#system-flagged-symbol
  fill: $level-high-color !important

#filter-symbol
  color: $text-color



  &.custom-filter-0
    color: #0091ea
    margin: -7px 0px 0px -7px


  &.custom-filter-1
    color: #fb8c00
    margin: -7px 0px 0px -7px

  &.custom-filter-2
    color: #00b8d4
    margin: -7px 0px 0px -7px

  &.custom-filter-3
    color: #9e9d24
    margin: -7px 0px 0px -7px

  &.custom-filter-4
    color: #43a047
    margin: -7px 0px 0px -7px



</style>

