<script>
export default {
  name: 'app-icon',
  components: {
  },
  props: {
    icon: null,
    iconClass: null,
    type: null,
    clazz: null,
    width: null,
    height: null,
    fill: null,
    level: null,
    significance: null,
    isSimpleMode: null
  },
  data () {
    return {
      clinvarLevels: {
        clinvar_path: 'high',
        clinvar_lpath: 'likely-high',
        clinvar_uc: "unknown",
        clinvar_benign: "low",
        clinvar_lbenign: "likely-low",
        clinvar_other: "other",
        clinvar_cd: "conflicting"
      }
    }
  },
  methods: {
    getClassObject: function(clazzes) {
      let theLevel = this.level
      if (this.icon == 'clinvar' && this.significance) {
        theLevel = this.clinvarLevels[this.significance];
      }
      var levelClass = theLevel ? 'level-' + theLevel : null;

      if (levelClass) {
        if (clazzes == null) {
          clazzes = [];
        }
        clazzes.push(levelClass);
      }

      return clazzes;

    },
    getContainerStyle: function() {
      let self = this;
      return {
        'display': 'inline-block',
        'min-width': self.width,
        'min-height': self.height
      }

    },
  },
  computed: {


    iconStyle: function() {
      return 'width:' + this.width + 'px;height:' + this.height + 'px';
    },
    containerStyle: function() {
      return 'display: inline-block;vertical-align: top; '
      + 'width:'  + this.width + 'px;'
      + 'height:' + this.height + 'px;';
      + 'min-width:'  + this.width + 'px;'
      + 'max-height:' + this.height + 'px;';
    }

  }
}
